mapper.update:
update comment set film_id = #{filmId}, score = #{score}, comment = #{comment}, user_id = #{userId},
time = #{time}, type = #{type}
where id = #{id}

mapper.select:
select comment.*, film.name as filmName, COALESCE(user.name, admin.name) as userName from comment left join film on comment.film_id = film.id left join user on comment.user_id = user.id left join admin on comment.user_id = admin.id
and film.name like concat('%', #{filmName}, '%') and comment.user_id = #{userId} and comment.type = #{type} and comment.film_id = #{filmId}
order by comment.id desc
